INSERT INTO `produk` (`id`, `kategori_id`, `nama_produk`, `deskripsi`, `harga`, `stok`, `gambar`, `berat`, `created_at`) VALUES
(1, 7, 'Timberland 6-Inch Premium Waterproof Boots', 'Ikon sepatu bot klasik dengan bahan kulit nubuck premium yang tahan air, sol karet tebal, dan kerah empuk untuk kenyamanan. Sangat tangguh dan stylish untuk segala cuaca.', 2800000, 10, '6856235f31234-Mens-Timberland-Premium-6Inch-Waterproof-Boot.png', 1000, '2025-06-21 03:13:35'),
(2, 7, 'Dr. Martens 1460 Smooth Leather', 'Sepatu bot legendaris dengan 8 lubang tali, kulit smooth leather yang kaku pada awalnya namun akan melunak seiring waktu, jahitan kuning khas, dan sol AirWair yang tahan minyak & lemak.', 2500000, 10, '685623d93230e-675a5ad3-7d9f-443d-a46c-e6efbbaea918.jpg', 1000, '2025-06-21 03:15:37'),
(3, 3, 'Nike Air Force 1 \'07', 'Sneaker ikonik yang awalnya adalah sepatu basket. Memiliki desain timeless dengan bahan kulit, sol tebal dengan teknologi Nike Air untuk bantalan, dan siluet yang mudah dipadukan.', 1500000, 10, '68562469c1080-images.jpg', 1000, '2025-06-21 03:17:19'),
(6, 7, 'Clarks Desert Boot', 'Terinspirasi dari sepatu bot pasar di Kairo, sepatu ini memiliki desain simpel dengan bahan suede atau kulit, dua lubang tali, dan sol krep (crepe) yang sangat nyaman untuk penggunaan sehari-hari.', 2500000, 21, '685912a323a47-26155527_GW_1.jpg', 1000, '2025-06-23 08:38:59'),
(7, 7, 'Red Wing Heritage Classic Moc Toe', 'Sepatu bot kerja dari Amerika yang sangat tahan lama. Dibuat dengan kulit berkualitas tinggi, konstruksi Goodyear welt, dan sol Traction Tred putih yang ikonik.', 4500000, 12, '6859135065020-RWH_08800_MUL _N1_0313.jpg', 1000, '2025-06-23 08:41:52'),
(8, 7, 'Caterpillar Colorado Boots', 'Dikenal dengan ketahanannya, sepatu bot ini memiliki tampilan rugged yang terinspirasi dari alat berat. Terbuat dari kulit full grain, sol tebal, dan cocok untuk aktivitas luar ruang.', 1900000, 15, '685913c59af30-download.jpg', 1000, '2025-06-23 08:43:49'),
(9, 1, 'G.H. Bass & Co. Weejuns Penny Loafers', 'Dianggap sebagai pencipta Penny Loafer modern. Memiliki desain klasik dengan strap kulit di bagian atas yang memiliki potongan khas. Terbuat dari kulit asli yang dipoles.', 2000000, 23, '68591426ea2e1-images.jpg', 1000, '2025-06-23 08:45:26'),
(10, 1, 'Gucci Jordaan Horsebit Loafer', 'Loafer mewah yang ikonik dengan detail horsebit (logam menyerupai sanggurdi kuda) di bagian atas. Terbuat dari kulit premium Italia, memberikan tampilan yang sangat mewah dan elegan.', 12000000, 1, '68591483d1b34-download.jpg', 1000, '2025-06-23 08:46:59'),
(11, 1, 'Cole Haan Pinch Penny Loafer', 'Menggabungkan desain tradisional dengan teknologi kenyamanan modern dari Cole Haan. Ringan, fleksibel, dan sangat nyaman untuk dipakai sepanjang hari di kantor atau acara kasual.', 1800000, 34, '685914ce4b113-download (1).jpg', 1000, '2025-06-23 08:48:14'),
(12, 1, 'Tod\'s Gommino Driving Shoes', 'Loafer super nyaman yang terinspirasi dari sepatu mengemudi. Ciri khasnya adalah sol karet pebbled yang memanjang hingga ke tumit, memberikan cengkeraman dan fleksibilitas.', 7000000, 24, '68591509a3deb-download (2).jpg', 1000, '2025-06-23 08:49:13'),
(13, 1, 'Bottega Veneta Intrecciato Loafer', 'Dikenal dengan teknik anyaman kulit khas Intrecciato. Loafer ini memancarkan kemewahan yang subtil dan keahlian tangan tingkat tinggi. Sangat fleksibel dan nyaman.', 13000000, 4, '6859153f0c96a-download (3).jpg', 1000, '2025-06-23 08:50:07'),
(14, 2, 'Hoka Clifton 9', 'Sepatu lari untuk latihan harian (daily trainer) yang sangat populer. Dikenal karena bantalannya yang maksimal namun tetap ringan, memberikan kenyamanan luar biasa untuk lari jarak jauh.', 2000000, 23, '68591591c4315-download (4).jpg', 1000, '2025-06-23 08:51:29'),
(15, 2, 'Nike Pegasus 41', 'Sepatu lari legendaris yang serbaguna, cocok untuk berbagai jenis pelari. Menggunakan busa ReactX dan unit Air Zoom untuk kombinasi bantalan dan responsivitas yang seimbang.', 2300000, 44, '685915d13e42f-download.jpg', 1000, '2025-06-23 08:52:33'),
(16, 2, 'Asics Gel-Kayano 31', 'Sepatu lari kategori stabilitas yang ideal untuk pelari dengan kaki overpronation. Memberikan dukungan dan bantalan premium dengan teknologi 4D Guidance System dan PureGEL.', 2500000, 23, '6859160669eba-download (1).jpg', 1000, '2025-06-23 08:53:26'),
(17, 2, 'New Balance Fresh Foam X 880v14', 'Workhorse yang andal untuk latihan lari harian. Menawarkan keseimbangan antara bantalan Fresh Foam X yang empuk dan daya tahan, menjadikannya pilihan aman untuk pelari netral.', 2100000, 21, '6859163b43f39-download (2).jpg', 1000, '2025-06-23 08:54:19'),
(18, 2, 'Adidas Adizero Adios Pro 3', 'Sepatu lari untuk kompetisi (race day) yang dirancang untuk kecepatan. Dilengkapi dengan Lightstrike Pro foam dan ENERGYRODS 2.0 (batang karbon) untuk tolakan yang maksimal.', 3800000, 19, '6859166cf1ad5-download (3).jpg', 1000, '2025-06-23 08:55:09'),
(19, 4, 'Vans Classic Slip-On Checkerboard', 'Ikon budaya pop dan skate. Terbuat dari kanvas dengan motif papan catur yang khas, kerah empuk, dan sol waffle karet yang legendaris. Sangat serbaguna dan santai.', 800000, 22, '685916b6228ca-download (5).jpg', 1000, '2025-06-23 08:56:22'),
(20, 4, 'TOMS Alpargata', 'Sepatu slip-on kanvas yang ringan dan ramah lingkungan. Dikenal dengan program sosial \"One for One\". Desainnya simpel dengan toe-stitch khas dan sol yang fleksibel.', 700000, 12, '685916ebe4bcb-download.jpg', 1000, '2025-06-23 08:57:15'),
(21, 4, 'Skechers Slip-ins: Ultra Flex 3.0', 'Dirancang untuk kemudahan maksimal dengan teknologi Heel Pillow. Pengguna bisa memakai sepatu ini sepenuhnya tanpa menggunakan tangan. Sangat ringan dan empuk.', 1000000, 11, '685917245e081-download (1).jpg', 1000, '2025-06-23 08:58:12'),
(22, 4, 'Converse Deck Star Slip \'67', 'Alternatif dari Converse yang lebih minimalis. Mengambil inspirasi dari sepatu dek tahun 60-an, memiliki siluet ramping dengan material kanvas dan sol karet khas Converse.', 750000, 33, '6859175c0fcb5-download (2).jpg', 1000, '2025-06-23 08:59:08'),
(23, 4, 'Birkenstock Boston', 'Gabungan antara sandal dan sepatu (clog). Terbuat dari kulit atau suede dengan footbed dari gabus-lateks yang dirancang secara anatomis untuk menopang kaki dengan sempurna.', 2500000, 11, '685917a8cc18c-download (3).jpg', 1000, '2025-06-23 09:00:24'),
(24, 3, 'Adidas Samba OG', 'Awalnya sepatu futsal, kini menjadi ikon fashion streetwear. Ciri khasnya adalah upper kulit dengan overlay suede di bagian depan (T-toe) dan sol karet gum berwarna coklat.', 1700000, 23, '6859182f0c8a2-download (4).jpg', 1000, '2025-06-23 09:02:39'),
(25, 3, 'Converse Chuck 70 High Top', 'Versi premium dan lebih detail dari Chuck Taylor All Star klasik. Menggunakan kanvas yang lebih tebal dan kuat, bantalan lebih empuk, dan detail vintage seperti plat lisensi hitam.', 1000000, 11, '685918618445c-download.jpg', 1000, '2025-06-23 09:03:29'),
(26, 3, 'New Balance 550', 'Sneaker yang terinspirasi dari sepatu basket era 80-an. Kembali populer karena desainnya yang retro, clean, dengan upper kulit dan siluet low-top yang chunky.', 1800000, 44, '685918998b72b-download (1).jpg', 1000, '2025-06-23 09:04:25'),
(27, 3, 'Adidas Stan Smith', 'Sneaker tenis klasik yang menjadi fashion staple. Dikenal dengan desainnya yang super minimalis: upper kulit putih, detail hijau di bagian tumit, dan tiga baris perforasi sebagai ganti logo stripes.', 1600000, 11, '685918dd1647e-download (2).jpg', 1000, '2025-06-23 09:05:33');
